import axios from 'axios'

export async function getToken() {
    try {
      const response = await axios.post('https://clothingstock-h5gjhdesbkfyedgn.eastus-01.azurewebsites.net/api/login',
        {
            "UserName" : "Julio",
            "PassWord" : "1234"
        }
      );
      return response.data.token;
    } catch (error) {
      console.error('Erro ao obter o token:', error);
      throw error; // Repassa o erro para ser tratado na função que chama
    }
  }
  
export async function Get_TShirts() {
    try {
      const token = await getToken();
      console.log('Token:', token);
  
      const response = await axios.get('https://clothingstock-h5gjhdesbkfyedgn.eastus-01.azurewebsites.net/api/T_Shirts', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      console.log('Dados:', response.data);
      return response.data
    } 

    catch (error) {
      console.error('Erro:', error);
    }
}

export async function PostRequest(clothing, type) {
  try{
    const token = await getToken()
    const response = await axios.post(`https://clothingstock-h5gjhdesbkfyedgn.eastus-01.azurewebsites.net/api/${type}`,clothing, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    return response.data;
  }
  catch(error)
  {
    console.error("Erro", error)
  }
  
}
