import { Get_TShirts} from "../Services/ApiConsumer"
import { useState, useEffect } from "react"
import Styles from "./ShowStock.module.css"
import ClothingForm from "./ClothingForm"
import { Link, BrowserRouter, Route, Routes } from "react-router-dom"

export default function ShowStock()
{
    const [data, setData] = useState([])
    const [loading, setLoading] = useState(true)
    const [error, setError] = useState(null)

    useEffect(() =>{
        async function fetchData() 
        {
            try{
                const data = await Get_TShirts()
                console.log(data)
                if(Array.isArray(data)){
                    console.log("SUCESSO")
                }
                setData(data)
            }
            catch(error){
                setError("Não foi possivel trazer os dados", error)
            }
            finally{
                setLoading(false)
            }
        }

        fetchData()
    },[])

    if(loading == true) return <h2>loading...</h2>
    if(error) return <h2>{error}</h2>
        return(
            <>
                bom dia
                <div className={Styles.card_container}>
                    {data.map((shirt) => (
                        <div key={shirt.Id} className={Styles.card}>
                            <div className={Styles.card_content}>
                                <p>{shirt.name} - {shirt.size}</p>
                                <Link to="/form"><button className={Styles.edit_button}>Editar</button></Link>
                            </div>
                        </div>
                    ))}
                </div>
            </>
        )

}
