import  ApiConsumer  from "../Services/ApiConsumer"
import { useState, useEffect } from "react"
import Styles from "./ShowStock.module.css"
import ClothingForm from "./ClothingForm"
import { Link, BrowserRouter, Route, Routes } from "react-router-dom"

export default function ShowStock()
{
    const [data, setData] = useState([])
    const [loading, setLoading] = useState(true)
    const [error, setError] = useState(null)

    useEffect(() =>{
        async function fetchData() 
        {
            
                const data = await ApiConsumer.Get()
                console.log(data)
                if(Array.isArray(data)){
                    console.log("SUCESSO")
                }
                setData(data)
            
                
                //setError("Não foi possivel trazer os dados", error)
           
                setLoading(false)
            
        }

        fetchData()
    },[])

    if(loading == true) return <h2>loading...</h2>
    if(error) {
        console.error(error)
        return <h2>{error}</h2>
    }
        return(
            <>
                bom dia
                <div className={Styles.card_container}>
                    {data.map((item) => (
                        <div key={item.Id} className={Styles.card}>
                            <div className={Styles.card_content}>
                                <p>{item.name} - {item.size}</p>
                                <Link to="/details" 
                                state={{item}}>
                                    <button className={Styles.edit_button}>Detalhes</button>
                                </Link>
                            </div>
                        </div>
                    ))}
                </div>
            </>
        )

}
