import style from "./ClothingForm.module.css"
import { useState, useEffect } from "react"
import { PostRequest } from "../Services/ApiConsumer"
import { useLocation } from "react-router-dom"


export default function ClothingForm(){

    const location = useLocation();

    const obj = location.state?.obj;
    const text = location.state?.text;
    const requestMethod = location.state?.requestMethod;

    console.log(" USE LOCATION ", obj, text, requestMethod);

    const [formInfo, setFormInfo] = useState(
        {
            name:" ",
            stock:" ",
            color:"",
            description:" ",
            size:" ",
            imageUrl:" "
        }
    )

    const [type, setType] = useState()

    useEffect(() => {
        if (obj) {
          setFormInfo({
            mame: obj.name || "",
            stock: obj.stock || "",
            color: obj.color || "",
            description: obj.description || "",
            size: obj.size || "",
            imageUrl: obj.imageUrl || "",
          });
        }
      }, [obj]);

    return(
        <div className={style.container}>
            <form className={style.clothingForm}>
            <h2>{text}</h2>
                <div>
                    <label>Prouct Name</label>
                    <input 
                        name="Name" 
                        type="text"
                        value={formInfo.name}
                        onChange={HandleInputChange}
                    />
                </div>
                <div>
                    <label>Stock</label>
                    <input 

                        name="Stock"
                        value={formInfo.stock}
                        type="text"
                        onChange={HandleInputChange}
                    />
                </div>
                <div>
                    <label>Color</label>
                    <input 
                        name="Color"
                        value={formInfo.color}
                        type="text"
                        onChange={HandleInputChange}
                        />
                </div>
                <div>
                    <label>Description</label>
                    <input 
                        name="Description"
                        placeholder={formInfo.description}
                        type="text"
                        onChange={HandleInputChange}
                        />
                </div>
                <div>
                    <label>Size</label>
                    <input 
                        name="Size"
                        value={formInfo.size}
                        type="text"
                        onChange={HandleInputChange}
                        />
                </div>
                <div>
                    <label>Image Url</label>
                    <input 
                        name="ImageUrl"
                        value={formInfo.imageUrl}
                        type="text"
                        onChange={HandleInputChange}
                        />
                </div>
                <div>
                    <label>Clothing Type</label>
                        <select
                            name="clothingType"
                            onChange={(e) => {setType(e.target.value)} }>
                                <option value="T-shirt">T_Shirt</option>
                                <option value="Shorts">Shorts</option>
                        </select>
                </div>
                <div>
                    <button className={style.submitButton} type="submit" onClick={GetFormInfo}>Submit</button>
                </div>

            </form>
            
        </div>
    )

    function HandleInputChange(e)
    {
        const{name, value} = e.target;
        setFormInfo({
            ...formInfo,
            [name]: value
        });
    }   

    function GetFormInfo(e, requestMethod)
    {
        e.preventDefault();
        if(requestMethod === "post")
        {
            PostRequest(formInfo, type)
        }

        else if(requestMethod === "put")
        {
            console.log("put")
        }
        
    }
}
