import { Get_TShirts} from "../Services/ApiConsumer"
import { useState, useEffect } from "react"

export default function ShowStock()
{
    const [data, setData] = useState([])
    const [loading, setLoading] = useState(true)
    const [error, setError] = useState(null)

    useEffect(() =>{
        async function fetchData() 
        {
            try{
                const data = await Get_TShirts()
                console.log(data)
                if(Array.isArray(data)){
                    console.log("SUCESSO")
                }
                setData(data)
            }
            catch(error){
                setError("Não foi possivel trazer os dados", error)
            }
            finally{
                setLoading(false)
            }
        }

        fetchData()
    },[])

    if(loading == true) return <h2>loading...</h2>
    if(error) return <h2>{error}</h2>
        return(
            <>
                bom dia
                <h1>Camisetas</h1>
                {data.map(shirt => {
                    <div>  
                        {shirt.name} - {shirt.size}
                    </div>
                })}
            </>
        )

}
