import { type } from "@testing-library/user-event/dist/type";
import { useLocation, Link } from "react-router-dom";
import { DeleteRequest } from "../Services/ApiConsumer"
import Modal from "./Modal";
import { useState, useEffect } from "react";

export default function EntityDetails()
{
    const [isOpen, setIsOpen] = useState(false)
    const location = useLocation();
    const obj = location.state?.shirt;
    console.log(obj)
    return(
        <>       
            <div>
                <ul>
                    <li>Id: {obj.id}</li>
                    <li>name: {obj.name}</li>
                    <li>stock: {obj.stock}</li>
                    <li>Description: {obj.description}</li>
                    <li>Size: {obj.size}</li>
                    <li>imageUrl: {obj.imageUrl}</li>
                </ul>
                <Link to="/new" state={{
                    Text: "editar",
                    obj: obj,
                    type:"T_Shirt" 
                }}>editar</Link>
                <button onClick={() => setIsOpen(true)}>Deletar</button>   
                
            </div>
                <Modal isOpen={isOpen} OnCancel={handleCancel} onConfirm={handleDelete}/>

        </>
            
    )
    function handleDelete(e)
    {
        DeleteRequest(obj.id, "T_Shirts")
        setIsOpen(false)
    }
    
    function handleCancel()
    {
        setIsOpen(false)
        console.log(isOpen)
    }
}
