import './App.css';
import axios from 'axios'
import {useState} from 'react'

function App() {
  Get_TShirts()
  return (
    <h1>Bem vindo</h1>
  )

}
function GetToken() {
const [token, setToken] = useState()

  axios.post('https://clothingstock-h5gjhdesbkfyedgn.eastus-01.azurewebsites.net/api/Login', {
    UserName: 'Julio',
    Password: '1234'
  })
  .then(response => {

    const token = response.data.token;
    console.log('Token:', token);
    setToken(token)

      // Agora você pode armazenar o token em localStorage ou outro local seguro
    localStorage.setItem('jwtToken', token);
  })
  return token;
}

function Get_TShirts()
{
  const Token = GetToken()

  axios.get("https://clothingstock-h5gjhdesbkfyedgn.eastus-01.azurewebsites.net/api/T_Shirts", {
    headers:{
      "Authorization" : `Bearer ${Token}`
    }
  }).then(response => {
    console.log(response)
  })
}
export default App;
