import './App.css';
import axios from 'axios'
import {useState} from 'react'

function App() {
  GetToken()
  return (
    <h1>Bem vindo</h1>
  )

}



 function GetToken() {
  const [token, setToken] = useState()

  axios.post("https://clothingstock-h5gjhdesbkfyedgn.eastus-01.azurewebsites.net/api/Login", {
    UserName :"Julio",
    Password :"1234"
  })
  .then(response => {
    const jwtToken = response.data.token;
    setToken(jwtToken); // Armazena o token no estado local
    localStorage.setItem('jwtToken', jwtToken); // Armazena no localStorage
    console.log('Token JWT:', jwtToken);
  })
  .catch(error => {
    console.error('Erro ao fazer login:', error.response ? error.response.data : error.message);
  });
};

export default App;
