import style from "./ClothingForm.module.css"
import { useState, useEffect } from "react"
import { PostRequest, PutRequest } from "../Services/ApiConsumer"
import { useLocation } from "react-router-dom"


export default function ClothingForm(){

    useEffect(() => {
        const location = useLocation();
        const obj = location.state?.obj;
        const text = location.state?.text;
    },[])


    console.log(" USE LOCATION ", obj, text);

    const [formInfo, setFormInfo] = useState(
        {
            name:" ",
            stock:" ",
            color:"",
            description:" ",
            size:" ",
            imageUrl:" "
        }
    )

    const [type, setType] = useState("T_Shirts")

    useEffect(() => {
        if (obj) {
          setFormInfo({
            id: obj.id || "",
            name: obj.name || "",
            stock: obj.stock || "",
            color: obj.color || "",
            description: obj.description || "",
            size: obj.size || "",
            imageUrl: obj.imageUrl || "",
          });
        }
      }, [obj]);

    return(

        <div className={style.container}>
            <h1>ID: {formInfo.id}</h1>
            <h1>metodo {location.state?.requestMethod}</h1>
            <form className={style.clothingForm}>
            <h2>{text}</h2>
                <div>
                    <label>Prouct Name</label>
                    <input 
                        name="Name" 
                        type="text"
                        placeholder={formInfo.name}
                        onChange={HandleInputChange}
                    />
                </div>
                <div>
                    <label>Stock</label>
                    <input 

                        name="Stock"
                        placeholder={formInfo.stock}
                        type="text"
                        onChange={HandleInputChange}
                    />
                </div>
                <div>
                    <label>Color</label>
                    <input 
                        name="Color"
                        placeholder={formInfo.color}
                        type="text"
                        onChange={HandleInputChange}
                        />
                </div>
                <div>
                    <label>Description</label>
                    <input 
                        name="Description"
                        placeholder={formInfo.description}
                        type="text"
                        onChange={HandleInputChange}
                        />
                </div>
                <div>
                    <label>Size</label>
                    <input 
                        name="Size"
                        placeholder={formInfo.size}
                        type="text"
                        onChange={HandleInputChange}
                        />
                </div>
                <div>
                    <label>Image Url</label>
                    <input 
                        name="ImageUrl"
                        placeholder={formInfo.imageUrl}
                        type="text"
                        onChange={HandleInputChange}
                        />
                </div>
                <div>
                    <label>Clothing Type</label>
                        <select
                            name="clothingType"
                            onChange={(e) => {setType(e.target.value)
                                
                            } }>
                                <option value="T_shirts">T Shirt</option>
                                <option value="Shorts">Shorts</option>
                        </select>
                </div>
                <div>
                    <button className={style.submitButton} type="submit" onClick={GetFormInfo}>Submit</button>
                </div>

            </form>
            
        </div>
    )

    function HandleInputChange(e)
    {
        const{name, value} = e.target;
        setFormInfo({
            ...formInfo,
            [name]: value
        });
    }   

    function GetFormInfo(e)
    {
        var requestMethod = location.state?.requestMethod

        e.preventDefault();
        if(requestMethod === "post")
        {
            PostRequest(formInfo, type)
        }

        else if(requestMethod === "put")
        {
            PutRequest(formInfo, type)
        } 
        
    }
}
