import axios from 'axios'
import {useState} from 'react'

export function GetToken() {
    const [token, setToken] = useState()
    
      axios.post('https://clothingstock-h5gjhdesbkfyedgn.eastus-01.azurewebsites.net/api/Login', {
        UserName: 'Julio',
        Password: '1234'
      })
      .then(response => {
        
        const token = response.data.token;
        console.log('Token:', token);
        setToken(token)

      })
      return token;
    }
    
export function Get_TShirts()
{
    const Token = GetToken()

    axios.get("https://clothingstock-h5gjhdesbkfyedgn.eastus-01.azurewebsites.net/api/T_Shirts", {
    headers:{
        "Authorization" : `Bearer ${Token}`
    }
    }).then(response => {
        console.log(response)
        return response.data 
    }).catch(error => {
        console.error("Erro na requisição", error)
    });
    
    
}
