import axios from 'axios'

export async function getToken() {
    try {
      const response = await axios.post('https://clothingstock-h5gjhdesbkfyedgn.eastus-01.azurewebsites.net/api/login',
        {
            "UserName" : "Julio",
            "PassWord" : "1234"
        }
      );
      return response.data.token;
    } 
    catch (error) {
      console.error('Erro ao obter o token:', error);
      throw error;
    }
  }
  
export async function Get_TShirts(type, id) {
    try {
      const token = await getToken();

      if(token)
      {
        console.log('Token:', token);
  
        const response = await axios.get('https://clothingstock-h5gjhdesbkfyedgn.eastus-01.azurewebsites.net/api/T_Shirts', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        console.log('Dados:', response.data);
        return response.data
      }

    } 

    catch (error) {
      console.error('Erro:', error);
    }
}

export async function PostRequest(clothing, type) {

  function removeProperty(obj, propToRemove) {
    const { [propToRemove]: _, ...newObj } = obj;
    return newObj;
  }

  const noIdEntity = removeProperty(clothing, "id"); // retira a prop id para ter exito na requisição

  try{
    console.log("TYPE: ", type)
    console.log("Clothing obj:", clothing)
    const token = await getToken();
    const response = await axios.post(`https://clothingstock-h5gjhdesbkfyedgn.eastus-01.azurewebsites.net/api/${type}`, noIdEntity, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    console.log(response.data)
    return (await response).data;

  }
  catch(error)
  {
    console.error("Erro", error)
  }
  
}

export async function PatchRequest(clothing, type) {
  
  const entityParams = {
    "id" : clothing.id,
    "name" : clothing.name || "",
    "stock" : clothing.stock || "",
    "description" : clothing.description || "",
    "size" : clothing.size || "",
    "imageurl" : clothing.imageurl || ""
  }

  console.log("Patch request entity params:", entityParams)
  try{
    const token = await getToken();
    const response = axios.patch(`https://clothingstock-h5gjhdesbkfyedgn.eastus-01.azurewebsites.net/api/${type}/${clothing.id}`, null, {
      params: entityParams,
      headers: {
          Authorization: `Bearer ${token}`
        }
      });
      return (await response).data;
  }
  catch(error)
  {
    console.error(error)
  }
  
}

export async function DeleteRequest(id, type)
{
  try{
    const token = await getToken()
    const response = axios.delete(`https://clothingstock-h5gjhdesbkfyedgn.eastus-01.azurewebsites.net/api/${type}/${id}`, { 
      headers: {
         Authorization: `Bearer ${token}`
      }
    });
    return (await response).data
  }
  catch(error)
  {
    console.log("Erro na requisição delete", error)
    throw error;
  }
 

}
