import { Get_TShirts} from "../Services/ApiConsumer"
import { useState, useEffect } from "react"
import Styles from "./ShowStock.module.css"

export default function ShowStock()
{
    const [data, setData] = useState([])
    const [loading, setLoading] = useState(true)
    const [error, setError] = useState(null)

    useEffect(() =>{
        async function fetchData() 
        {
            try{
                const data = await Get_TShirts()
                console.log(data)
                if(Array.isArray(data)){
                    console.log("SUCESSO")
                }
                setData(data)
            }
            catch(error){
                setError("Não foi possivel trazer os dados", error)
            }
            finally{
                setLoading(false)
            }
        }

        fetchData()
    },[])

    if(loading == true) return <h2>loading...</h2>
    if(error) return <h2>{error}</h2>
        return(
            <>
                bom dia
                {data.map((shirt) => (
                    <div key={shirt.Id} className={Styles.card}>
                        <div className={Styles.card_content}>
                            <p>{shirt.name} - {shirt.size}</p>
                            <button className={Styles.edit_button}>Editar</button>
                        </div>
                    </div>
                ))}
            </>
        )

}
